import { FormEvent } from "react";

function Form(): JSX.Element {

    const forms = [
        {
            type: "text",
            name: "Name"
        },
        {
            type: "email",
            name: "Email"
        },
        {
            type: "text",
            name: "Subject"
        }
    ]
    return (
        <form
            className="contact-form form"
            action="#"
            method="post"
            id="contact-form"
            onSubmit={(evt: FormEvent<HTMLFormElement>) => {
                evt.preventDefault();
            }}
        >
            {forms.map(form => (
                <input
                    key={form.name}
                    className="form-input"
                    name={form.name}
                    type={form.type}
                    id="contact-form"
                />
            ))}

            <textarea
                className="contact__textarea form__textarea"
                id="contact"
                name="contact"
                placeholder="Tell how was your stay, what you like and what can be improved"
                value="{formState.review}"
            />
            <button
                className="reviews__submit form__submit button"
                type="submit"
            >
                Submit
            </button>
        </form>
    )
}

export default Form;
